const track=document.getElementById("carouselTrack"),speed=.5;let position=0;function duplicateContent(){const t=track.scrollWidth,e=window.innerWidth;for(;track.scrollWidth<e*2;)track.innerHTML+=track.innerHTML}duplicateContent();function animate(){position-=speed,Math.abs(position)>=track.scrollWidth/2&&(position=0),track.style.transform=`translateX(${position}px)`,requestAnimationFrame(animate)}animate(),window.addEventListener("resize",()=>{track.style.transform="translateX(0)",position=0})